<section class="border-t-2 border-gray-200 w-full my-2">
    <h4 class="font-bold py-2 font-sans text-xl">Tools Used</h4>
    <ul class="flex  flex-row w-full gap-4">
        {#each tools as tool}
        <li class="w-8 underline underline-offset-1 decoration-black hover:cursor-pointer">
           <!-- Iterate over toolIcons and find the one that matches 'tool' and show that icon -->
            {#each toolIcons as icons}
                {#if icons.name == tool}
                    {@html icons.icon}
                {/if}
            {/each}
        </li>
        {/each}
    </ul>
</section>